import com.google.glassware.WebUtil;

public class RunStory extends HttpServlet {
	private static final long serialVersionUID = 1L;
	private static final Logger LOG = Logger.getLogger(RunStory.class.getSimpleName());

	@Override
	protected void doGet(HttpServletRequest req, HttpServletResponse res) throws IOException {
		LOG.info("arrived at GET request");
	    String userId = AuthUtil.getUserId(req);
	    Credential credential = AuthUtil.newAuthorizationCodeFlow().loadCredential(userId);

	    LOG.info("passed credential request");
	    LOG.info("STORY URL: "+req.getParameter("story"));
	    
	    ZMachineGlass zm = new ZMachineGlass(userId,credential);

//	    String state = "";
	    if (req.getParameter("action") != null) {
	    	zm.load(req.getParameter("story"));
	    	zm.play(req.getParameter("action"));
	    } else {
		    zm.start(req.getParameter("story"));
	    }

		try {
			MirrorClient.insertSubscription(credential,WebUtil.buildUrl(req, "/notify"), userId,"timeline");
		} catch (GoogleJsonResponseException e) {
			LOG.warning("Could not subscribe " + WebUtil.buildUrl(req, "/notify") + " because "
					+ e.getDetails().toPrettyString());
		}

//	    res.setContentType("text/plain");
//	    res.setCharacterEncoding("UTF-8");
//	    PrintWriter out = res.getWriter();
//		out.println(restore);
	
	}
}
